import { Component, Input } from '@angular/core';
import { Product, products } from '../products';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  @Input() producto!: Product; 

  products = [...products];

    onNotify() {
    window.alert('Seras notificado cuando el producto este en venta');
  }

  share() {
    window.alert('Has compartido este producto!');
  }
}
<p-card> <ng-template pTemplate="header">
     <h3> <a [title]="'Detalles ' + producto.name"> {{ producto.name }} </a> 
    </h3> </ng-template>
      <p *ngIf="producto.description">Descripción: {{ producto.description }}
      </p> <button type="button">Compartir</button> 


      <ng-template pTemplate="footer">
     <app-product-alerts [product]="producto">
         </app-product-alerts> </ng-template> </p-card>
